import React, { useState } from 'react';
import MusicList from '../components/MusicList';
import PlayerControls from '../components/PlayerControls';
import FooterPlayer from '../components/FooterPlayer';
import { musicData } from '../utils/musicData';

const Home = () => {
    const [currentMusic, setCurrentMusic] = useState(musicData[0]);
    const [isPlaying, setIsPlaying] = useState(false);

    const handlePlayPauseClick = () => {
        setIsPlaying(!isPlaying);
    };

    const handleNextClick = () => {
        const currentIndex = musicData.indexOf(currentMusic);
        setCurrentMusic(musicData[(currentIndex + 1) % musicData.length]);
    };

    const handlePrevClick = () => {
        const currentIndex = musicData.indexOf(currentMusic);
        setCurrentMusic(musicData[(currentIndex - 1 + musicData.length) % musicData.length]);
    };

    return (
        <div className="home">
            <MusicList musicData={musicData} onSelectMusic={setCurrentMusic} />
            <PlayerControls
                isPlaying={isPlaying}
                onPlayPauseClick={handlePlayPauseClick}
                onNextClick={handleNextClick}
                onPrevClick={handlePrevClick}
            />
            <FooterPlayer currentMusic={currentMusic} />
        </div>
    );
};

export default Home;
