import React, { useState, useRef, useEffect } from 'react';
import PlayerControls from './PlayerControls';
import SongDetails from './SongDetails';
import Playlist from './Playlist';
import goatFirstSingle from '../assets/songs/Goat First Single Promo.mp3';
import mattaMatta from '../assets/songs/Matta Matta Raaja Matta.mp3';
import whistlePodu from '../assets/songs/Whistle Podu.mp3';

const songs = [
  { title: 'Goat First Single Promo', src: goatFirstSingle },
  { title: 'Matta Matta Raaja Matta', src: mattaMatta },
  { title: 'Whistle Podu', src: whistlePodu },
];

function MusicPlayer() {
  const [currentSongIndex, setCurrentSongIndex] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(new Audio(songs[currentSongIndex].src));

  useEffect(() => {
    audioRef.current.src = songs[currentSongIndex].src; // Update the audio source when the song changes
    if (isPlaying) {
      audioRef.current.play();
    }
  }, [currentSongIndex]);

  const togglePlayPause = () => {
    setIsPlaying(!isPlaying);
    if (!isPlaying) {
      audioRef.current.play();
    } else {
      audioRef.current.pause();
    }
  };

  const playNext = () => {
    setCurrentSongIndex((prevIndex) => (prevIndex + 1) % songs.length);
  };

  const playPrevious = () => {
    setCurrentSongIndex((prevIndex) => (prevIndex - 1 + songs.length) % songs.length);
  };

  return (
    <div className="music-player">
      <SongDetails song={songs[currentSongIndex]} />
      <PlayerControls
        isPlaying={isPlaying}
        onPlayPause={togglePlayPause}
        onNext={playNext}
        onPrevious={playPrevious}
      />
      <Playlist songs={songs} onSelectSong={setCurrentSongIndex} />
    </div>
  );
}

export default MusicPlayer;
