import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';
import './videoRoomStyles.css';

const socket = io('http://localhost:5000');

function VideoRoom({ roomId, userName }) {
  const [users, setUsers] = useState([]);
  const videoRefs = useRef({});
  const peerConnections = {};

  useEffect(() => {
    const userId = Math.random().toString(36).substring(2);
    const currentTime = new Date().toLocaleTimeString();

    socket.emit('join-room', roomId, userId, userName, currentTime);

    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then(stream => {
      videoRefs.current[userId] = { stream, userName, joinTime: currentTime };
      updateUserVideos();

      socket.on('user-connected', (userId, userName, joinTime) => {
        videoRefs.current[userId] = { userName, joinTime };
        const peerConnection = new RTCPeerConnection();
        stream.getTracks().forEach(track => peerConnection.addTrack(track, stream));
        peerConnections[userId] = peerConnection;

        updateUserVideos();
      });

      socket.on('user-disconnected', (userId) => {
        if (peerConnections[userId]) peerConnections[userId].close();
        delete peerConnections[userId];
        delete videoRefs.current[userId];
        updateUserVideos();
      });
    });

    const updateUserVideos = () => {
      setUsers(Object.entries(videoRefs.current).map(([id, details]) => ({
        userId: id,
        userName: details.userName,
        joinTime: details.joinTime,
        stream: details.stream
      })));
    };

  }, [roomId, userName]);

  return (
    <div className="video-room">
      {users.map(({ userId, userName, joinTime, stream }, index) => (
        <div key={userId} className="video-container">
          <video
            ref={(videoEl) => {
              if (videoEl && !videoEl.srcObject) {
                videoEl.srcObject = stream;
                videoEl.play();
              }
            }}
            autoPlay playsInline
          />
          <div className="user-details">
            <p>{userName}</p>
            <small>Joined at {joinTime}</small>
          </div>
        </div>
      ))}
    </div>
  );
}

export default VideoRoom;
