import React, { useEffect, useState } from 'react';
import { io } from 'socket.io-client';
import VideoStream from './components/VideoStream';
import './styles.css';

const socket = io('http://localhost:5000'); // Server URL

function App() {
  const [streams, setStreams] = useState([]);

  useEffect(() => {
    // Handle new user connection
    socket.on('user-connected', (userId, joinTime) => {
      const newStream = {
        userId,
        joinTime,
        stream: null, // Replace with actual media stream later
      };
      setStreams((prev) => [...prev, newStream]);
    });

    socket.on('user-disconnected', (userId) => {
      setStreams((prev) => prev.filter((stream) => stream.userId !== userId));
    });
  }, []);

  return (
    <div className="container">
      {streams.map((stream, index) => (
        <VideoStream
          key={index}
          stream={stream.stream}
          userId={stream.userId}
          joinTime={stream.joinTime}
        />
      ))}
    </div>
  );
}

export default App;
