import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';
import './videoRoomStyles.css';

const socket = io('http://localhost:5000');

function VideoRoom({ roomId, userName }) {
  const [users, setUsers] = useState([]);
  const localStream = useRef(null);
  const videoRefs = useRef({});
  const peerConnections = useRef({});

  useEffect(() => {
    const userId = Math.random().toString(36).substring(2);
    const currentTime = new Date().toLocaleTimeString();

    socket.emit('join-room', roomId, userId, userName, currentTime);

    const setupLocalStream = async () => {
      localStream.current = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });

      // Update local video in UI
      videoRefs.current[userId] = { stream: localStream.current, userName, joinTime: currentTime };
      setUsers([{ userId, userName, joinTime: currentTime, stream: localStream.current }]);

      socket.on('user-connected', (userId, userName, joinTime) => {
        const peerConnection = new RTCPeerConnection();
        localStream.current.getTracks().forEach(track => peerConnection.addTrack(track, localStream.current));
        peerConnections.current[userId] = peerConnection;

        // Handle remote streams
        peerConnection.ontrack = (event) => {
          if (!videoRefs.current[userId]) {
            videoRefs.current[userId] = { userName, joinTime, stream: event.streams[0] };
            setUsers(users => [...users, { userId, userName, joinTime, stream: event.streams[0] }]);
          }
        };

        // Send offer
        peerConnection.createOffer().then(offer => peerConnection.setLocalDescription(offer));
        socket.emit('offer', userId, offer);
      });

      socket.on('offer', async (userId, offer) => {
        const peerConnection = new RTCPeerConnection();
        peerConnections.current[userId] = peerConnection;
        peerConnection.ontrack = (event) => {
          if (!videoRefs.current[userId]) {
            videoRefs.current[userId] = { userName: '', joinTime: '', stream: event.streams[0] };
            setUsers(users => [...users, { userId, userName: '', joinTime: '', stream: event.streams[0] }]);
          }
        };
        peerConnection.setRemoteDescription(new RTCSessionDescription(offer));
        const answer = await peerConnection.createAnswer();
        await peerConnection.setLocalDescription(answer);
        socket.emit('answer', userId, answer);
      });

      socket.on('answer', (userId, answer) => {
        peerConnections.current[userId].setRemoteDescription(new RTCSessionDescription(answer));
      });

      socket.on('user-disconnected', (userId) => {
        if (peerConnections.current[userId]) peerConnections.current[userId].close();
        delete peerConnections.current[userId];
        setUsers(users => users.filter(user => user.userId !== userId));
      });
    };

    setupLocalStream();

    return () => {
      if (localStream.current) {
        localStream.current.getTracks().forEach(track => track.stop());
      }
      Object.values(peerConnections.current).forEach(pc => pc.close());
    };
  }, [roomId, userName]);

  return (
    <div className="video-room">
      {users.map(({ userId, userName, joinTime, stream }, index) => (
        <div key={userId} className="video-container">
          <video
            ref={(videoEl) => {
              if (videoEl && !videoEl.srcObject) {
                videoEl.srcObject = stream;
                videoEl.play();
              }
            }}
            autoPlay playsInline
          />
          <div className="user-details">
            <p>{userName}</p>
            <small>Joined at {joinTime}</small>
          </div>
        </div>
      ))}
    </div>
  );
}

export default VideoRoom;
