import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import JoinRoomForm from './components/JoinRoomForm';
import VideoStream from './components/VideoStream';

const socket = io.connect();

const App = () => {
  const [inRoom, setInRoom] = useState(false);
  const [userStreams, setUserStreams] = useState({});
  const [localStream, setLocalStream] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then((stream) => {
      setLocalStream(stream);
    });
  }, []);

  const joinRoom = (roomId, username, password) => {
    socket.emit('join-room', roomId, username, password);

    socket.on('password-error', (errMsg) => {
      setError(errMsg);
    });

    socket.on('user-connected', (userId) => {
      console.log(`${userId} connected`);
    });

    setInRoom(true);
  };

  return (
    <div className="app">
      {inRoom ? (
        <div className="video-grid">
          <VideoStream stream={localStream} username="You" />
          {Object.keys(userStreams).map((userId) => (
            <VideoStream key={userId} stream={userStreams[userId]} username={userId} />
          ))}
        </div>
      ) : (
        <JoinRoomForm joinRoom={joinRoom} />
      )}
      {error && <div className="error-message">{error}</div>}
    </div>
  );
};

export default App;
