// app.js
import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';
import './style.css';

const socket = io();

function App() {
  const [stream, setStream] = useState(null);
  const [peers, setPeers] = useState({});
  const videoRefs = useRef({});
  const localVideoRef = useRef(null);

  useEffect(() => {
    const initializeMedia = async () => {
      try {
        const localStream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
        setStream(localStream);
        localVideoRef.current.srcObject = localStream;
      } catch (err) {
        console.error('Failed to get local stream', err);
      }
    };

    initializeMedia();

    socket.on('offer', handleOffer);
    socket.on('answer', handleAnswer);
    socket.on('candidate', handleCandidate);

    return () => {
      socket.off('offer', handleOffer);
      socket.off('answer', handleAnswer);
      socket.off('candidate', handleCandidate);
    };
  }, []);

  const handleOffer = async (data) => {
    const { id, offer } = data;
    const peerConnection = createPeerConnection(id);
    peerConnection.setRemoteDescription(new RTCSessionDescription(offer));
    const answer = await peerConnection.createAnswer();
    await peerConnection.setLocalDescription(answer);
    socket.emit('answer', { id, answer });
  };

  const handleAnswer = (data) => {
    const { id, answer } = data;
    peers[id].setRemoteDescription(new RTCSessionDescription(answer));
  };

  const handleCandidate = (data) => {
    const { id, candidate } = data;
    peers[id].addIceCandidate(new RTCIceCandidate(candidate));
  };

  const createPeerConnection = (id) => {
    const peerConnection = new RTCPeerConnection();
    peerConnection.onicecandidate = (event) => {
      if (event.candidate) {
        socket.emit('candidate', { id, candidate: event.candidate });
      }
    };
    peerConnection.ontrack = (event) => {
      if (!videoRefs.current[id]) {
        videoRefs.current[id] = document.createElement('video');
        videoRefs.current[id].autoPlay = true;
        document.getElementById('videos').appendChild(videoRefs.current[id]);
      }
      videoRefs.current[id].srcObject = event.streams[0];
    };
    stream.getTracks().forEach(track => peerConnection.addTrack(track, stream));
    setPeers(prevPeers => ({ ...prevPeers, [id]: peerConnection }));
    return peerConnection;
  };

  const handleJoin = async () => {
    const peerConnection = createPeerConnection();
    const offer = await peerConnection.createOffer();
    await peerConnection.setLocalDescription(offer);
    socket.emit('offer', { offer });
  };

  return (
    <div className="app">
      <div className="local-video">
        <video ref={localVideoRef} autoPlay muted></video>
      </div>
      <div id="videos" className="remote-videos"></div>
      <button className="join-button" onClick={handleJoin}>Join</button>
    </div>
  );
}

export default App;
