import React, { useEffect, useState } from 'react';
import { io } from 'socket.io-client';
import VideoStream from './components/VideoStream';
import './styles.css';

const socket = io('http://localhost:5000'); // Backend URL

function App() {
  const [streams, setStreams] = useState([]);
  const [inRoom, setInRoom] = useState(false);
  const [roomId, setRoomId] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  // Function to handle form submission and join the room
  const handleJoinRoom = () => {
    if (password === 'PH@1314') {
      socket.emit('join-room', roomId, 'User_' + Math.floor(Math.random() * 100), password);
      setInRoom(true); // Set inRoom to true after joining the room
    } else {
      setErrorMessage('Invalid password');
    }
  };

  useEffect(() => {
    // Listen for when another user joins
    socket.on('user-connected', (userId, joinTime) => {
      console.log('User connected:', userId, 'at', joinTime);
      const newStream = {
        userId,
        joinTime,
        stream: 'mock-stream', // Placeholder for testing
      };
      setStreams((prev) => [...prev, newStream]);
    });

    // Listen for when a user disconnects
    socket.on('user-disconnected', (userId) => {
      setStreams((prev) => prev.filter((stream) => stream.userId !== userId));
    });

    // Cleanup on component unmount
    return () => {
      socket.off('user-connected');
      socket.off('user-disconnected');
    };
  }, []);

  return (
    <div className="container">
      {!inRoom ? (
        <div className="form-box">
          <h2>Join Room</h2>
          <input
            type="text"
            placeholder="Enter Room ID"
            value={roomId}
            onChange={(e) => setRoomId(e.target.value)}
          />
          <input
            type="password"
            placeholder="Enter Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button onClick={handleJoinRoom}>Join Room</button>
          {errorMessage && <p className="error">{errorMessage}</p>}
        </div>
      ) : (
        <div className="video-grid">
          {streams.length > 0 ? (
            streams.map((stream, index) => (
              <VideoStream
                key={index}
                stream={stream.stream}
                userId={stream.userId}
                joinTime={stream.joinTime}
              />
            ))
          ) : (
            <p>No users in the room yet. Waiting for participants...</p>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
